/*全体の設定
---------------------------------------------------------------------------*/
body {
	margin: 0px;
	padding: 0px;
	color: #666;	/*全体の文字色*/
	font-size: 15px;	/*文字サイズ*/
	line-height: 2;		/*行間*/
}

h1,h2,h3,h4,h5,p,ul,ol,li,dl,dt,dd,form,figure,form {margin: 0px;padding: 0px;font-size: 100%;}
ul {list-style-type: none;}
ol {padding-left: 40px;padding-bottom: 15px;}
img,svg {border: none;max-width: 100%;height: auto;vertical-align: middle;}
table {border-collapse:collapse;font-size: 100%;border-spacing: 0;}
iframe {width: 100%;}

/*リンク（全般）設定
---------------------------------------------------------------------------*/
a {
	color: #666;	/*リンクテキストの色*/
	transition: 0.5s;	/*マウスオン時の移り変わるまでの時間設定。*/
}
a:hover {
	color: #2799b5;			/*マウスオン時の文字色*/
	text-decoration: none;	/*マウスオン時に下線を消す設定*/
}

/*inner共通
---------------------------------------------------------------------------*/
.inner {
	margin: 0 auto;
	padding: 0 2%;		/*上下、左右へのボックス内の余白*/
}

/*メインメニュー
---------------------------------------------------------------------------*/
/*メニューブロック*/
#menubar {
    clear: both;overflow: hidden;
    background: #ff8d0b;	/*背景色（オレンジ）*/
}
/*メニュー１個あたりの設定*/
#menubar li {
	float: left;	/*左に回り込み*/
	width: 16.66%;	/*メニュー幅（100÷6個=16.66%）　４個にする場合　100÷4=25%*/
}
#menubar li a {
	display:  block;text-decoration: none;
    text-align: center;	/*文字をセンタリング*/
    font-size: 20px;			/*文字サイズ*/
	padding: 20px 0;	/*上下、左右へのメニュー内への余白*/
	color: #fff;	/*文字色*/
}
/*１つ目のメニューへの追加設定*/
#menubar li:first-child a {
	border-left: 1px solid #000;	/*メニューの線の幅、線種、色*/
}
/*装飾文字（英語表記）の設定*/
#menubar li a span {
	text-shadow: none;display: block;
	font-size: 20px;			/*文字サイズ*/
	font-weight: normal;	/*文字を太字でなく標準に戻す設定*/
	letter-spacing: 0.2em;	/*文字間隔を少しあける設定*/
	opacity: 0.5;			/*透明度50％*/
}
/*マウスオン時メニューの設定*/
#menubar li a:hover {
	background: #FFFFFF;	/*背景色*/
	color: #16a6b6; /*文字色*/
}

/*スマホ用メニューを表示させない*/
#menubar-s {display: none;}
/*３本バーアイコンを表示させない*/
#menubar_hdr {display: none;}

/*contents（３カラムを囲むブロック）
---------------------------------------------------------------------------*/
#contents {
	clear: both;overflow: hidden;
	padding-top: 40px;		/*上に空けるボックス内の余白*/
    padding-bottom: 40px;	/*下に空けるボックス内の余白*/
    background: #ffa012;	/*背景色（オレンジ） */

}

/*contents-in（mainとsubを囲むブロック）
---------------------------------------------------------------------------*/
#contents-in {
	float: left;	/*左に回り込み*/
	width: 80%;		/*ブロックの幅*/
}

/*mainコンテンツ（中央のブロック）
---------------------------------------------------------------------------*/
#main {
	float: right;	/*右に回り込み*/
	width: 75%;		/*ブロックの幅*/
}
/*mainコンテンツのh2タグ設定*/
#main h2 {
	text-align: center;	/*文字をセンタリング*/
	background-image: linear-gradient(45deg, #709dff 0%, #b0c9ff 100%);
	padding: 0.5em 1em;	/*上下、左右への余白*/
	border-radius: 6px 6px 0px 0px;	/*角丸のサイズ。左上、右上、右下、左下への設定*/
    margin-bottom: 10px;
    font-weight: bold;
	color: #fff;	/*文字色*/
}

/*subコンテンツ（左側のブロック）
---------------------------------------------------------------------------*/
#sub {
	float: left;	/*左に回り込み*/
	width: 22.5%;	/*ブロックの幅*/
}

/*sideコンテンツ（右側のブロック）
---------------------------------------------------------------------------*/
#side {
	float: right;	/*右に回り込み*/
	width: 18%;		/*ブロックの幅*/
}

/*sub,side共通設定
---------------------------------------------------------------------------*/
/*h2見出しタグ設定*/
#sub h2,
#side h2 {
	text-align: center;	/*文字をセンタリング*/
	background-image: linear-gradient(45deg, #709dff 0%, #b0c9ff 100%);
	padding: 0.5em 1em;	/*上下、左右への余白*/
	border-radius: 6px 6px 0px 0px;	/*角丸のサイズ。左上、右上、右下、左下への設定*/
	margin-bottom: 10px;
    font-weight: bold;
    color: #fff;	/*文字色*/
    min-width: 50%;
}

/*notice設定（お知らせ欄）
---------------------------------------------------------------------------*/
/*notice設定*/
#sub .notice,
#side .notice {
	border: 1px solid #ccc;	/*枠線の幅、線種、色*/
	padding: 10px;			/*ボックス内の余白*/
    margin-bottom: 20px;	/*ボックスの下にあけるスペース*/
    background: #fff;
}
/*noticeの中にあるh2タグの設定*/
#sub .notice h2,
#side .notice h2 {
	border-radius: 0;	/*角丸をなくす設定*/
}

/*sub,sideコンテンツ内のおすすめ一覧ブロック
---------------------------------------------------------------------------*/
/*各ボックスの設定*/
#sub .list, #side .list {
	position: relative;overflow: hidden;
	font-size: 11px;	/*文字サイズ*/
	line-height: 1.2;	/*行間を狭くする*/
	border-top: 1px solid #ccc;	/*上の線の幅、線種、色*/
    padding: 10px;	/*ボックス内の余白*/
    background: #fff;
}
#sub .list a, #side .list a {
	text-decoration: none;display: block;overflow: hidden;
}
/*最後ボックスの設定*/
#sub .list:last-of-type, #side .list:last-of-type {
	border-bottom: 1px solid #ccc;	/*下の線の幅、線種、色*/
	margin-bottom: 20px;	/*下に空けるスペース*/
}
/*ボックス内のh4タグ設定*/
#sub .list h4, #side .list h4 {
  font-size: 15px;	/*文字サイズ*/
	font-weight: bold;	/*太字にする設定*/
}
/*ボックス内のp(段落)タグ設定*/
#sub .list p, #side .list p {
	padding: 0 !important;
}
/*ボックス内の写真設定*/
#sub .list figure img, #side .list figure img {
	float: left;	/*画像を左へ回り込み*/
    display: block;
    width: 50px;
    height: 50px;
    padding: 10px;
}

/*画面幅800px以下の設定
------------------------------------------------------------------------------------------------------------------------------------------------------*/
@media screen and (max-width:800px){

    /*スマホ用メニューブロック*/
    #menubar-s {
        display: block;overflow: hidden;
        position: absolute;
        top: 100px;	/*上から100pxの場所に配置*/
        border-top: 1px solid #fff;		/*上の線の幅、線種、色*/
        width: 100%;
        z-index: 10;
        animation-duration: 0.5S;	/*アニメーションの実行時間。0.5秒。*/
        animation-fill-mode: both;	/*アニメーションの完了後、最後のキーフレームを維持する*/
    }
    /*メニュー１個あたりの設定*/
    #menubar-s li a {
        display: block;text-decoration: none;
        padding: 15px 10px 15px 20px;	/*上、右、下、左へのメニュー内の余白*/
        border-bottom: 1px solid #fff;	/*下の線の幅、線種、色*/
        background: #ffb0b0;	/*背景色（淡いピンク） */
    /* background: #dcf2e1;	背景色（淡いグリーン） */
        font-size: 20px;
        color: #fff;	/*文字色*/
    clear: both;overflow: hidden;
    }

    #menubar-s li a:hover {
        background: #FFFFFF;	/*背景色*/
        color: #16a6b6; /*文字色*/
    }

    /*英語表記（飾り文字）*/
    #menubar-s li a span {
        display: block;
        font-size: 12px;	/*文字サイズ*/
    }
    /*PC用メニューを非表示にする*/
    #menubar {display: none;}

    /*main,sub,side,contents-in
    ---------------------------------------------------------------------------*/
    #main, #sub, #side, #contents-in {
        float: none;
        width: auto;
    }
}

